vector&lt;int&gt; postorderTraversal(TreeNode* root) &#123; vector&lt;int&gt; ans; stack&lt;TreeNode*&gt;stk; TreeNode* prev=nullptr; while(!stk.empty()||root!=nullptr)&#123; while(root!=nullptr)&#123; stk.emplace(root); root=root-&gt;left; &#125; root=stk.top(); if(root-&gt;right==nullptr || root-&gt;right==prev)&#123; ans.push_back(root-&gt;val); prev=root; stk.pop(); root=nullptr; // ËøôÈáåÂæàÈáçË¶ÅÔºÅÂê¶Âàô‰ºöÂõ†‰∏∫root‰∏ç‰∏∫Á©∫ËÄå‚ÄúÂÖ•Ê†à-Âá∫Ê†à‚ÄùÊó†ÈôêÂæ™ÁéØ &#125; else&#123; root=root-&gt;right; &#125; &#125; return ans; &#125;

