    classDiagram
    class Iterator {
        &lt;&lt;interface&gt;&gt;
        +IsEnd() bool
        +GetCur() E
    }
    class Collection {
        &lt;&lt;interface&gt;&gt;
        +CreateIterator() Iterator[E]
    }
    class TreeNode {
        +Val T
        +Left *TreeNode[T]
        +Right *TreeNode[T]
    }
    class BinaryTree {
        +Root *TreeNode[T]  
    }
    class InOrderIterator {
        -stack []*TreeNode[T]
        -pushLeft(node *TreeNode[T])
        +IsEnd() bool
        +GetCur() *TreeNode[T]+
    }
