3. ËßÇÂØüËÄÖÊ®°ÂºèÁöÑÂÆûÁé∞ÔºàgoÔºâtype Observer interface &#123;    Update(event string, data any)&#125;type Subject interface &#123;    Register(o Observer)    Unregister(o Observer)    Notify(event string, data any)&#125;type Topic struct &#123;    observers []Observer&#125;func (t *Topic) Register(o Observer) &#123;    t.observers = append(t.observers, o)&#125;func (t *Topic) Unregister(o Observer) &#123;    i := -1    for idx, ob := range t.observers &#123;        if ob == o &#123;            i = idx            break        &#125;    &#125;    if i &gt;= 0 &#123;        t.observers = append(t.observers[:i], t.observers[i+1:]...)    &#125;&#125;func (t *Topic) Notify(event string, data any) &#123;    for _, o := range t.observers &#123;        o.Update(event, data)    &#125;&#125;type PrintObserver struct &#123;    name string&#125;func (p *PrintObserver) Update(event string, data any) &#123;    fmt.Printf(&quot;[%s] %s: %v\n&quot;, p.name, event, data)&#125;

